from __future__ import annotations

import copy

import pytest

from src.prompt.enums import ErrorCategory
from src.prompt.validators import DocumentIssues, ErrorRecord, validate_llm_output


def _build_valid_record() -> dict[str, str | int]:
    return {
        "rule_from_tool": "Some rule",
        "type_from_tool": "grammar",
        "message_from_tool": "Example message",
        "suggestions_from_tool": "Try this",
        "context_from_tool": "Context sample",
        "error_category": ErrorCategory.PARSING_ERROR.value,
        "confidence_score": 82,
        "reasoning": "Clear mechanical issue",
    }


def test_error_category_metadata() -> None:
    machine_keys = [member.value for member in ErrorCategory]
    assert machine_keys == [
        "parsing_error",
        "spelling_error",
        "absolute_grammatical_error",
        "possible_ambiguous_grammatical_error",
        "stylistic_preference",
        "false_positive",
    ]

    first_key, label, description = next(iter(ErrorCategory.prompt_rows()))
    assert first_key == ErrorCategory.PARSING_ERROR.value
    assert label == ErrorCategory.PARSING_ERROR.display
    assert "Mechanical" in description or "mechanical" in description


def test_validate_llm_output_success() -> None:
    payload = {
        "page_1": {
            "sample.pdf": [
                _build_valid_record(),
            ]
        }
    }

    document, errors = validate_llm_output(payload)
    assert errors == []
    assert document is not None
    assert isinstance(document, DocumentIssues)
    record = document.root["page_1"].root["sample.pdf"][0]
    assert isinstance(record, ErrorRecord)
    assert record.error_category is ErrorCategory.PARSING_ERROR


@pytest.mark.parametrize(
    "mutator, expected_fragment",
    [
        (lambda p: p["page_1"]["sample.pdf"][0].__setitem__("error_category", "bad_cat"), "error_category"),
        (lambda p: p.__setitem__("1", p.pop("page_1")), "page_"),
        (lambda p: p["page_1"].__setitem__("", p["page_1"].pop("sample.pdf")), "Filenames"),
    ],
)
def test_validate_llm_output_failures(mutator, expected_fragment) -> None:
    payload = {
        "page_1": {
            "sample.pdf": [
                _build_valid_record(),
            ]
        }
    }
    mutated = copy.deepcopy(payload)
    mutator(mutated)

    document, errors = validate_llm_output(mutated)
    assert document is None
    assert any(expected_fragment in err for err in errors)
